                    success := mload(0)        // Set `success = returndata` of external call
                }
                default {                     // This is an excessively non-compliant ERC-20, revert.
                    revert(0, 0)
                }
        }
        require(success, "TOKEN_TRANSFER_OUT_FAILED");
    }
}

/**
 * @title Compound's CEther Contract
 * @notice CToken which wraps Ether
 * @author Compound
 */
contract CEther is CToken {
    /**
     * @notice Construct a new CEther money market
     * @param comptroller_ The address of the Comptroller
     * @param interestRateModel_ The address of the interest rate model
     * @param initialExchangeRateMantissa_ The initial exchange rate, scaled by 1e18
     * @param name_ ERC-20 name of this token
     * @param symbol_ ERC-20 symbol of this token
     * @param decimals_ ERC-20 decimal precision of this token
     * @param admin_ Address of the administrator of this token
     */
    constructor(ComptrollerInterface comptroller_,
                InterestRateModel interestRateModel_,
                uint initialExchangeRateMantissa_,
                string memory name_,
                string memory symbol_,
                uint8 decimals_,
                address payable admin_) public {
        // Creator of the contract is admin during initialization
        admin = msg.sender;

        initialize(comptroller_, interestRateModel_, initialExchangeRateMantissa_, name_, symbol_, decimals_);

        // Set the proper admin now that initialization is done
        admin = admin_;
    }


    /*** User Interface ***/

    /**
     * @notice Sender supplies assets into the market and receives cTokens in exchange
     * @dev Reverts upon any failure
     */
    function mint() external payable {
        (uint err,) = mintInternal(msg.value);
        requireNoError(err, "mint failed");
    }

    /**
     * @notice Sender redeems cTokens in exchange for the underlying asset
     * @dev Accrues interest whether or not the operation succeeds, unless reverted
     * @param redeemTokens The number of cTokens to redeem into underlying
     * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function redeem(uint redeemTokens) external returns (uint) {
        return redeemInternal(redeemTokens);
    }

    /**
     * @notice Sender redeems cTokens in exchange for a specified amount of underlying asset
     * @dev Accrues interest whether or not the operation succeeds, unless reverted
     * @param redeemAmount The amount of underlying to redeem
     * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function redeemUnderlying(uint redeemAmount) external returns (uint) {
        return redeemUnderlyingInternal(redeemAmount);
    }

    /**
      * @notice Sender borrows assets from the protocol to their own address
      * @param borrowAmount The amount of the underlying asset to borrow
      * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
      */
    function borrow(uint borrowAmount) external returns (uint) {
        return borrowInternal(borrowAmount);
    }

    /**
     * @notice Sender repays their own borrow
     * @dev Reverts upon any failure
     */
    function repayBorrow() external payable {
        (uint err,) = repayBorrowInternal(msg.value);
        requireNoError(err, "repayBorrow failed");
    }

    /**
     * @notice Sender repays a borrow belonging to borrower
     * @dev Reverts upon any failure
     * @param borrower the account with the debt being payed off
     */
    function repayBorrowBehalf(address borrower) external payable {
        (uint err,) = repayBorrowBehalfInternal(borrower, msg.value);
        requireNoError(err, "repayBorrowBehalf failed");
    }

    /**
     * @notice The sender liquidates the borrowers collateral.
     *  The collateral seized is transferred to the liquidator.
     * @dev Reverts upon any failure
     * @param borrower The borrower of this cToken to be liquidated
     * @param cTokenCollateral The market in which to seize collateral from the borrower
     */
    function liquidateBorrow(address borrower, CToken cTokenCollateral) external payable {
        (uint err,) = liquidateBorrowInternal(borrower, msg.value, cTokenCollateral);
        requireNoError(err, "liquidateBorrow failed");
    }

    /**
     * @notice Send Ether to CEther to mint
     */
    function () external payable {
        (uint err,) = mintInternal(msg.value);
        requireNoError(err, "mint failed");
    }

    /*** Safe Token ***/

    /**
     * @notice Gets balance of this contract in terms of Ether, before this message
     * @dev This excludes the value of the current message, if any
     * @return The quantity of Ether owned by this contract
     */
    function getCashPrior() internal view returns (uint) {
        (MathError err, uint startingBalance) = subUInt(address(this).balance, msg.value);
        require(err == MathError.NO_ERROR);
        return startingBalance;
    }

    /**
     * @notice Checks whether the requested transfer matches the `msg`
     * @dev Does NOT do a transfer
     * @param from Address sending the Ether
     * @param amount Amount of Ether being sent
     * @return Whether or not the transfer checks out
     */
    function checkTransferIn(address from, uint amount) internal view returns (Error) {
        // Sanity checks
        require(msg.sender == from, "sender mismatch");
        require(msg.value == amount, "value mismatch");
        return Error.NO_ERROR;
    }

    /**
     * @notice Perform the actual transfer in, which is a no-op
     * @param from Address sending the Ether
     * @param amount Amount of Ether being sent
     * @return The actual amount of Ether transferred
     */
    function doTransferIn(address from, uint amount) internal returns (uint) {
        // Sanity checks
        require(msg.sender == from, "sender mismatch");
        require(msg.value == amount, "value mismatch");
        return amount;
    }

    function doTransferOut(address payable to, uint amount) internal {
        /* Send the Ether, with minimal gas and revert on failure */
        to.transfer(amount);
    }

    function requireNoError(uint errCode, string memory message) internal pure {
        if (errCode == uint(Error.NO_ERROR)) {
            return;
        }

        bytes memory fullMessage = new bytes(bytes(message).length + 5);
        uint i;

        for (i = 0; i < bytes(message).length; i++) {
            fullMessage[i] = bytes(message)[i];
        }

        fullMessage[i+0] = byte(uint8(32));
        fullMessage[i+1] = byte(uint8(40));
        fullMessage[i+2] = byte(uint8(48 + ( errCode / 10 )));
        fullMessage[i+3] = byte(uint8(48 + ( errCode % 10 )));
        fullMessage[i+4] = byte(uint8(41));

        require(errCode == uint(Error.NO_ERROR), string(fullMessage));
    }
}

contract Compound is Ownable, ILiquidator, ILender, Exponential, CanReclaimTokens {
    using SafeMath for uint256;
    using SafeERC20 for ERC20;
    using Lender for Lender.State;
    using Liquidator for Liquidator.State;

    Lender.State private lenderState;
    Liquidator.State private liquidatorState;

    CEther public cEther;
    mapping (address=>CErc20) public cTokens;

    constructor() public {
        lenderState.init(msg.sender);
        liquidatorState.init(msg.sender);
    }

    function () external payable {
        if (msg.sender != address(cEther)) {
            lend(address(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE), msg.value);
        }
    }

    function updateRebalancer(address _newRebalancer) external onlyOwner {
        liquidatorState.updateRebalancer(_newRebalancer);
    }

    function updateLiquidityProvider(address _newLiquidityProvider) external onlyOwner {
        lenderState.updateLiquidityProvider(_newLiquidityProvider);
    }

    function unbalancedBalance(address _token) external view returns (uint256) {
        liquidatorState.balanceOf(_token);
    }

    function register(address _underlying, address _cToken) external onlyOwner {
        require(_underlying != address(0x0), "Underlying asset cannot be 0x0");
        address[] memory cTokenAddrs = new address[](1);
        cTokenAddrs[0] = _cToken;
        if (isEther(_underlying)) {
            cEther = CEther(uint256(_cToken));
        } else {
            cTokens[_underlying] = CErc20(_cToken);
        }
        CToken(_cToken).comptroller().enterMarkets(cTokenAddrs);
    }

    function deregister(address _underlying) external onlyOwner {
        if (isEther(_underlying)) {
            cEther.comptroller().exitMarket(address(cEther));
            delete(cEther);
        } else {
            cTokens[_underlying].comptroller().exitMarket(address(cTokens[_underlying]));
            delete(cTokens[_underlying]);
        }
    }

    function lend(address _token, uint256 _amount) public payable returns (uint256) {
        uint256 initialBalance;
        uint256 finalBalance;
        if (isEther(_token)) {
            initialBalance = cEther.balanceOf(address(this));
            require(_amount == msg.value, "invalid lend amount");
            cEther.mint.value(_amount)();
            finalBalance = cEther.balanceOf(address(this));
        } else {
            initialBalance = cTokens[_token].balanceOf(address(this));
            ERC20(_token).safeApprove(address(cTokens[_token]), _amount);
            require(cTokens[_token].mint(_amount) == 0, "failed to lend erc20 tokens");
            finalBalance = cTokens[_token].balanceOf(address(this));
        }
        lenderState.lend(_token, _amount, finalBalance - initialBalance);
        return (finalBalance - initialBalance);
    }

    function redeem(address payable _to, address _token, uint256 _amount)
        external isLiquidityProvider(lenderState, msg.sender) returns (uint256) {
        uint256 redeemAmount = withdraw(_to, _token, _amount);
        lenderState.redeem(_token, redeemAmount, _amount);
        return redeemAmount;
    }

    function borrow(address payable _to, address _token, uint256 _amount) external onlyOwner {
        borrowInternal(_to, _token, _amount);
        Lender.borrow(_token, _amount);
    }

    function borrowInternal(address payable _to, address _token, uint256 _amount) internal {
        if (isEther(_token)) {
            // Borrow Ethereum from Compound.
            require(cEther.borrow(_amount) == 0, "failed to borrow Ether");

            if (_to != address(this)) {
                // Transfer Ethereum to the `_to` address if it is not this contract's address.
                _to.transfer(_amount);
            }
        } else {
            // Borrow liquidityToken from Compound.
            require(cTokens[_token].borrow(_amount) == 0, "failed to borrow ERC20 tokens");

            if (_to != address(this)) {
                // Transfer liquidityTokens to adapter contract.
                ERC20(_token).safeTransfer(_to, _amount);
            }
        }
    }

    function repay(address _token, uint256 _amount) external payable {
        if (isEther(_token)) {
            require(_amount == msg.value, "invalid lend amount");
            cEther.repayBorrow.value(_amount)();
        } else {
            ERC20(_token).safeApprove(address(cTokens[_token]), _amount);
            require(cTokens[_token].repayBorrow(_amount) == 0, "failed to repay erc20 tokens");
        }
        Lender.repay(_token, _amount);
    }

    function valueOf(CToken _token, uint256 _amount) private view returns (uint256) {
        Exp memory exchangeRate = Exp({mantissa: _token.exchangeRateStored()});
        (MathError mErr, uint balance) = mulScalarTruncate(exchangeRate, _amount);
        require(mErr == MathError.NO_ERROR, "balance could not be calculated");
        return balance;
    }

    function balanceOf(address _token) external view returns (uint256) {
        return lenderState.balanceOf(_token);
    }

    function balanceOfUnderlying(address _token) external view returns (uint256) {
        CToken cToken;
        if (isEther(_token)) {
            cToken = cEther;
        } else {
            cToken = cTokens[_token];
        }
        return valueOf(cToken, cToken.balanceOf(address(this)));
    }

    function borrowBalanceOf(address _token) external view returns (uint256) {
        if (isEther(_token)) {
            return cEther.borrowBalanceStored(address(this));
        } else {
            return cTokens[_token].borrowBalanceStored(address(this));
        }
    }

    function mutBalanceOfUnderlying(address _token) external returns (uint256) {
        if (isEther(_token)) {
            return cEther.balanceOfUnderlying(address(this));
        } else {
            return cTokens[_token].balanceOfUnderlying(address(this));
        }
    }

    function mutBorrowBalanceOf(address _token) external returns (uint256) {
        if (isEther(_token)) {
            return cTokens[_token].borrowBalanceCurrent(address(this));
        } else {
            return cTokens[_token].borrowBalanceCurrent(address(this));
        }
    }

    function liquidate(
        address _borrower,
        address _collateralMarket,
        address _borrowedMarket,
        uint256 _minAmount,
        bytes calldata
    ) external payable {
        borrowInternal(address(this), _borrowedMarket, _minAmount);
        CToken collateralToken;
        if (isEther(_collateralMarket)) {
            collateralToken = cEther;
        } else {
            collateralToken = cTokens[_collateralMarket];
        }

        uint256 initialAmount = collateralToken.balanceOf(address(this));
        if (isEther(_borrowedMarket)) {
            cEther.liquidateBorrow.value(_minAmount)(_borrower, collateralToken);
        } else {
            CErc20 cerc20 = cTokens[_borrowedMarket];
            ERC20(_borrowedMarket).safeApprove(address(cerc20), _minAmount);
            require(cerc20.liquidateBorrow(_borrower, _minAmount, collateralToken) == 0, "liquidation failed");
        }
        uint256 finalAmount = collateralToken.balanceOf(address(this));
        liquidatorState.liquidate(_borrowedMarket, _minAmount, _collateralMarket, finalAmount-initialAmount);
    }

    function take(address _token, uint256 _amount) external isRebalancer(liquidatorState, msg.sender) returns (uint256) {
        liquidatorState.take(_token, _amount);
        return withdraw(msg.sender, _token, _amount);
    }

    function withdraw(address payable _to, address _token, uint256 _amount) internal returns (uint256) {
        uint256 initialBalance;
        uint256 finalBalance;
        if (isEther(_token)) {
            initialBalance = address(this).balance;
            cEther.redeem(_amount);
            finalBalance = address(this).balance;
            _to.transfer(finalBalance - initialBalance);
        } else {
            initialBalance = ERC20(_token).balanceOf(address(this));
            require(cTokens[_token].redeem(_amount) == 0, "failed to redeem erc20 tokens");
            finalBalance = ERC20(_token).balanceOf(address(this));
            ERC20(_token).safeTransfer(_to, finalBalance - initialBalance);
        }
        return (finalBalance - initialBalance);
    }

    function isEther(address _addr) public pure returns (bool) {
        return (_addr == address(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE));
    }
}
