pragma solidity ^0.5.0;


contract IEther is ERC20, ERC20Detailed, ReentrancyGuard, Ownable {
  using SafeERC20 for IERC20;
  using Address for address;
  using SafeMath for uint256;

  uint256 private pool;
  address public ZAP_ADDRESS;
  address public ETH_ADDRESS;

  constructor () public ERC20Detailed("iEther", "iETH", 18) {
      ZAP_ADDRESS = address(0x05A3c16C454262254c63b4ddA9ef4007799Af165);
      ETH_ADDRESS = address(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE);
  }

  function set_new_ZAP_ADDRESS(address _new_ZAP_ADDRESS) public onlyOwner {
      ZAP_ADDRESS = _new_ZAP_ADDRESS;
  }

  function set_new_ETH_ADDRESS(address _new_ETH_ADDRESS) public onlyOwner {
      ETH_ADDRESS = _new_ETH_ADDRESS;
  }

  function() external payable {

  }

  // Invest ETH
  function invest()
      external
      payable
      nonReentrant
  {
      require(msg.value > 0, "deposit must be greater than 0");

      pool = calcPoolValueInETH();
      // Send tokens via CDAI Zap
      uint expectedLiquidityTokens = UniSwap_ETH_CDAIZap(ZAP_ADDRESS).LetsInvest.value(msg.value)(address(this), 0);
      require(expectedLiquidityTokens > 0, "invalid liquidity tokens made available");

      // Calculate pool shares
      uint256 shares = 0;
      if (pool == 0) {
        shares = msg.value;
        pool = msg.value;
      } else {
        shares = (msg.value.mul(_totalSupply)).div(pool);
      }
      pool = calcPoolValueInETH();
      _mint(msg.sender, shares);
  }

  // Invest self eth from external profits
  function investSelf()
      external
      nonReentrant
      onlyOwner
  {
      uint balance = address(this).balance;
      require(balance > 0, "deposit must be greater than 0");
      uint expectedLiquidityTokens = UniSwap_ETH_CDAIZap(ZAP_ADDRESS).LetsInvest.value(balance)(address(this), 0);
      require(expectedLiquidityTokens > 0, "invalid liquidity tokens made available");
      pool = calcPoolValueInETH();
  }

  function calcPoolValueInETH() public view returns (uint) {
    if (pool == 0) {
      return 0;
    }
    uint totalShares = UniSwap_ETH_CDAIZap(ZAP_ADDRESS).uniBalanceOf(address(this));
    uint totalCDAI = UniSwap_ETH_CDAIZap(ZAP_ADDRESS).cBalanceOf(address(this));
    uint totalCDAIInETH = 0;
    if (totalCDAI > 0) {
      totalCDAIInETH = UniSwap_ETH_CDAIZap(ZAP_ADDRESS).getTokenToEthOutputPrice(totalCDAI);
    }
    uint eth = address(this).balance;
    uint totalSharesInETH = 0;
    if (totalShares > 0) {
      (totalSharesInETH,,) = UniSwap_ETH_CDAIZap(ZAP_ADDRESS).calcReturnETHFromShares(totalShares);
    }
    return (eth.add(totalSharesInETH).add(totalCDAIInETH));
  }

  function getPricePerFullShare() public view returns (uint) {
    uint _pool = calcPoolValueInETH();
    return _pool.mul(1e18).div(_totalSupply);
  }

  // Redeem any invested tokens from the pool
  function redeem(uint256 _shares)
      external
      nonReentrant
  {
      require(_shares > 0, "withdraw must be greater than 0");

      uint256 ibalance = balanceOf(msg.sender);
      require(_shares <= ibalance, "insufficient balance");

      pool = calcPoolValueInETH();
      // Calc eth to redeem before updating balances
      uint256 ethToRedeem = (pool.mul(_shares)).div(_totalSupply);


      _balances[msg.sender] = _balances[msg.sender].sub(_shares, "redeem amount exceeds balance");
      _totalSupply = _totalSupply.sub(_shares);

      emit Transfer(msg.sender, address(0), _shares);

      // Check ETH balance
      uint256 ethBalance = address(this).balance;
      if (ethBalance < ethToRedeem) {
        uint conversionPortion = SafeMath.div(SafeMath.mul(ethToRedeem.sub(ethBalance), 505), 1000);
        uint tokensToSell = UniSwap_ETH_CDAIZap(ZAP_ADDRESS).calcReturnSharesFromETH(conversionPortion);
        ethToRedeem = UniSwap_ETH_CDAIZap(ZAP_ADDRESS).Redeem(address(this), tokensToSell);
      }

      (bool result, ) = msg.sender.call.value(ethToRedeem)("");
      require(result, "transfer of ETH failed");
  }

  // incase of half-way error
  function inCaseTokenGetsStuck(IERC20 _TokenAddress) onlyOwner public {
      uint qty = _TokenAddress.balanceOf(address(this));
      _TokenAddress.transfer(msg.sender, qty);
  }
  // incase of half-way error
  function inCaseETHGetsStuck() onlyOwner public{
      (bool result, ) = msg.sender.call.value(address(this).balance)("");
      require(result, "transfer of ETH failed");
  }
}
