    string public constant symbol = "HOTO";
    uint8 public constant decimals = 0;

    uint256 private constant INITIAL_SUPPLY = 10 * (10 ** 6);

    constructor(address _honestoAccount, address _tokengateAccount) public
    {
        require(_honestoAccount != address(0), "_honestoAccount cannot be zero address");
        require(_tokengateAccount != address(0), "_tokengateAccount cannot be zero address");
        require(_honestoAccount != _tokengateAccount, "_honestoAccount and _tokengateAccount must be different");

        // make _honestoAccount and _tokengateAccount WhitelistAdmins
        _addWhitelistAdmin(_honestoAccount);
        _addWhitelistAdmin(_tokengateAccount);

        // make _honestoAccount and _tokengateAccount Whitelisters
        _addWhitelister(_honestoAccount);
        _addWhitelister(_tokengateAccount);
        renounceWhitelistAdmin();

        // set _honestAccount as the whitelist admin that can't revoke
        _setNonRevocableWhitelistAdmin(_honestoAccount);
        assert(super.getNonRevocableWhitelistAdmin() != address(0));
        // whitelist _honestoAccount so it can receive tokens
        _addWhitelisted(_honestoAccount);

        // make _honestoAccount the only Pauser
        _addPauser(_honestoAccount);
        renouncePauser();

        // assign total supply to _honestoAccount
        _mint(_honestoAccount, INITIAL_SUPPLY);
    }

    /**
     * @dev Transfer non-revocable whitelist admin status to another account
     * @param account The account to make the non-revocable whitelist admin
     */
