    onlyWhitelistAdmin {
        require(isWhitelistAdmin(account), "Cannot set non-revocable whitelist admin to a non-admin");
        super.setNonRevocableWhitelistAdmin(account);
    }

    /**
     * @dev Renounce own whitelist admin status. We need to overwrite this
     * method to add the onlyRevocableWhitelistAdmin modifier
     */
    function renounceWhitelistAdmin() public onlyRevocableWhitelistAdmin {
        super.renounceWhitelistAdmin();
    }

    /**
     * @dev Transfer token to a whitelisted address
     * @param to The address to transfer to.
     * @param value The amount to be transferred.
     */
    function transfer(address to, uint256 value) public
        onlyWhitelisted
        validDestination(to) returns (bool) {
        require(isWhitelisted(to), "recipient is not whitelisted");

        return super.transfer(to, value);
    }


    /**
     * @dev Transfer token from an address to another one given sufficient approval
     * @param to The address to transfer from.
     * @param to The address to transfer to.
     * @param value The amount to be transferred.
     */
    function transferFrom(address from, address to, uint256 value) public
        validDestination(to) returns (bool) {
        require(isWhitelisted(from), "sender is not whitelisted");
        require(isWhitelisted(to), "recipient is not whitelisted");

        return super.transferFrom(from, to, value);
    }

    /**
    * @dev Prevent transferring tokens to the contract address
    * @param to The address to check
    */
    modifier validDestination(address to) {
        require(to != address(this), "No transfer to the token smart contract");
        _;
    }

}
