// File: contracts/BoothRenting.sol

pragma solidity ^0.5.16;






contract BoothRenting is Initializable, Ownable, Pausable {
  using SafeMath for uint256;
  //using Address for address;

  struct Booth {
    address artistAddress;
    string artistName;
    string url;
    uint256 endTime;
  }

  mapping(uint256 => Booth) public rentedBooth;

  address payable public beneficiary;
  uint256 public pricePerDay;
  uint256 public maxDuration;
  uint256 public minDuration;

  event PriceChanged(uint256 pricePerDay);
  event BoothRented(uint256 id, uint256 endTime);

  function initialize(
    address payable _beneficiary,
    uint256 _pricePerDay,
    uint256 _minDuration,
    uint256 _maxDuration
  )
  public
  initializer
  {
    Ownable.initialize(msg.sender);
    Pausable.initialize(msg.sender);
    beneficiary = _beneficiary;
    pricePerDay = _pricePerDay;
    minDuration = _minDuration;
    maxDuration = _maxDuration;
  }

  function setPricePerDay(uint256 _pricePerDay) public onlyOwner {
    pricePerDay = _pricePerDay;
    emit PriceChanged(pricePerDay);
  }

  function setMinDuration(uint256 _minDuration) public onlyOwner {
    require(_minDuration < 365 days, "The minimum duration should be less than 1 year");
    require(_minDuration < maxDuration, "The minimum duration should less than the max duration");
    minDuration = _minDuration;
  }

  function setMaxDuration(uint256 _maxDuration) public onlyOwner {
    require(_maxDuration < 365 days, "The max duration should be less than 1 year");
    require(minDuration < _maxDuration, "The max duration should higher than the min duration");
    maxDuration = _maxDuration;
  }

  function changeBeneficiary(address payable _beneficiary) public onlyOwner {
    beneficiary = _beneficiary;
  }

  function rentBooth(uint256 _id, string memory _artistName, string memory _url) public whenNotPaused payable {
    require(msg.value > 0);
    Booth memory booth = rentedBooth[_id];
    require(booth.endTime < block.timestamp, "Booth already rented");
    uint duration = msg.value.div(pricePerDay).mul(1 days);
    require(duration > minDuration);
    require(duration < maxDuration);
    (bool success, ) = beneficiary.call.value(msg.value)("");
    require(success, "Transfer failed.");
    emit BoothRented(_id, 1);
    rentedBooth[_id].artistAddress = msg.sender;
    rentedBooth[_id].artistName = _artistName;
    rentedBooth[_id].url = _url;
    rentedBooth[_id].endTime = now+duration;
  }
}
