// File: contracts/Withdrawable.sol

pragma solidity ^0.5.0;




contract Withdrawable is Ownable {
    using SafeERC20 for IERC20;

    function withdraw(address asset) onlyOwner public {
        if (asset == address(0)) {
            msg.sender.transfer(address(this).balance);
        } else {
            IERC20 token = IERC20(asset);
            token.safeTransfer(msg.sender, token.balanceOf(address(this)));
        }
    }
}

// File: contracts/BurnAccounting.sol

pragma solidity ^0.5.0;






contract BurnAccounting is Withdrawable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    address edo;

    mapping (address => uint) public burnedBy;

    event Burn(address account, uint amount);

    constructor(address edoTokenAddress) public {
        edo = edoTokenAddress;
    }

    function burn(uint amount) public {
        IERC20(edo).safeTransferFrom(msg.sender, address(this), amount);
        require(IEDOToken(edo).burn(amount), 'cannot burn');
        burnedBy[msg.sender] = burnedBy[msg.sender].add(amount);
        emit Burn(msg.sender, amount);
    }

}
