{"Fabric.sol":{"content":"pragma solidity 0.5.11;

import "./Withdrawer.sol";


contract Fabric {
    function createContract(uint256 salt) public {
        bytes memory code = type(Withdrawer).creationCode;
        assembly {
            let codeSize := mload(code)
            let newAddr := create2(0, add(code, 32), codeSize, salt)
        }
    }
}
"},"IERC20.sol":{"content":"pragma solidity 0.5.11;


/**
 * @title ERC20 interface
 * @dev see https://github.com/ethereum/EIPs/issues/20
 */
interface IERC20 {
    function transfer(address to, uint256 value) external returns (bool);
    function approve(address spender, uint256 value) external returns (bool);
    function transferFrom(address from, address to, uint256 value) external returns (bool);
    function totalSupply() external view returns (uint256);
    function balanceOf(address who) external view returns (uint256);
    function allowance(address owner, address spender) external view returns (uint256);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}"},"Withdrawer.sol":{"content":"pragma solidity 0.5.11;

import "./IERC20.sol";


contract Withdrawer {

    address constant internal DGTX = 0x1C83501478f1320977047008496DACBD60Bb15ef; // for tests: 0x0045a2eBfFE0Cd3395c68fC124B1ED98b5B52c37
    address payable constant internal HOT_WALLET = 0xe3229A304165341EdFa7dd078030b13F87cA65E4; // for tests: 0xF33FEBF3069984bf26FfA9bf92097174DeD1DeeE

    constructor() public {
        uint256 balanceDGTX = IERC20(DGTX).balanceOf(address(this));
        IERC20(DGTX).transfer(HOT_WALLET, balanceDGTX);
        selfdestruct(HOT_WALLET);
    }
}
