contract SwapToken { // Beta program for trustless ERC20 token swaps. Use at own risk!
    using SafeMath for uint256;
    
    uint256 public swapsRegistered; 
    mapping (uint256 => Swap) public swaps; 
    
    event Registered(address indexed partyA, address indexed partyB, uint256 indexed swapNumber);
    event Executed(uint256 indexed swapNumber);
    
    struct Swap {
        address partyA;
        uint256 partyAswap;
    	IERC20 partyAtkn;
    	address partyB;
    	uint256 partyBswap;
    	IERC20 partyBtkn;
        uint256 swapNumber;
    	uint256 swapTermination;
        string details;
        bool executed;
    }
    
    function registerSwap( 
    	uint256 partyAswap,
    	IERC20 partyAtkn,
    	address partyB,
    	uint256 partyBswap,
    	IERC20 partyBtkn,
    	uint256 duration,
        string memory details) public {
        uint256 swapNumber = swapsRegistered.add(1); 
        uint256 swapTermination = now.add(duration); 
        swapsRegistered = swapsRegistered.add(1); 
        
        swaps[swapNumber] = Swap( 
            msg.sender,
            partyAswap,
            partyAtkn,
            partyB,
            partyBswap,
            partyBtkn,
            swapNumber,
            swapTermination,
            details,
            false);
        
        emit Registered(msg.sender, partyB, swapNumber); 
    }
    
    function executeSwap(uint256 swapNumber) public { 
        Swap storage swap = swaps[swapNumber]; 
        require(msg.sender == swap.partyB);
        require(swap.executed == false); 
        require(now <= swap.swapTermination); 
        
        swap.partyAtkn.transferFrom(swap.partyA, msg.sender, swap.partyAswap);
        swap.partyBtkn.transferFrom(msg.sender, swap.partyA, swap.partyBswap);
        
        swap.executed = true;
        emit Executed(swapNumber); 
    }
}
