    bool public closed; // public status of offer closure / default = "false" / adjustable by offeror-owner
    
    /***************
    OFFER DETAILS
    ***************/
    uint256 public RR; // counter for offerToken redemption requests
    string public offer; // stored OfferToken terms / e.g., IPFS hash
    
    mapping (uint256 => requests) public redemptions; 
    
    event Redeemed(address indexed redeemer, uint256 indexed index);
    event Responded(address indexed responder, uint256 indexed index);
    event Reviewed(address indexed reviewer, uint256 indexed index);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    
    struct requests { // RR: redemption requests stored on burn of OfferToken at registered rate
        address requester; 
        uint256 rNumber; 
        uint256 timeStamp; 
        string details; 
        string response; 
        string review;
        bool responded;
    }
    
    constructor(
        address _lexDAO,
        address payable _offeror,
        uint8 _decimals,
        uint256 cap,
