pragma solidity ^0.5.16;


contract DailyAction is Ownable, Pausable {
    using ECDSA for bytes32;

    event Action(address indexed user, int64 at);

    address public validator;
    uint256 public maxRefundValue = 0.001 ether;
    uint256 public gasOffset = 32329;
    mapping(address => int64) public lastActionDate;

    constructor(address _varidator) public {
        validator = _varidator;
    }

    function () external payable {}

    function withdraw() external onlyOwner() {
        _msgSender().transfer(address(this).balance);
    }

    function setValidator(address _validator) external onlyOwner() {
        validator = _validator;
    }

    function setGasOffset(uint256 _gasOffset) external onlyOwner() {
        gasOffset = _gasOffset;
    }

    function setMaxRefundValue(uint256 _maxRefundValue) external onlyOwner() {
        maxRefundValue = _maxRefundValue;
    }

    function request(bytes calldata _signature, int64 _time) external whenNotPaused() {
        uint256 startGas = gasleft();
        requestInner(_signature, _time);
        startGas = startGas + gasOffset;
        uint256 gasUsed = startGas - gasleft();
        uint256 refundValue = gasUsed * tx.gasprice;
        if (refundValue > maxRefundValue) {
            refundValue = maxRefundValue;
        }
        _msgSender().transfer(refundValue);
    }

    function requestInner(bytes memory _signature, int64 _time) internal {
        require(validateSig(_msgSender(), _time, _signature), "invalid signature");
        int64 day = (_time + 32400) / 86400; // UTC+9
        require(lastActionDate[_msgSender()] < day, "already requested today");
        lastActionDate[_msgSender()] = day;
        emit Action(_msgSender(), _time);
    }

    function validateSig(address _from, int64 _time, bytes memory _signature) public view returns (bool) {
        require(validator != address(0), "validator is not set");
        bytes32 hash = keccak256(abi.encode(_from, _time)).toEthSignedMessageHash();
        address signer = hash.recover(_signature);
        return (signer == validator);
    }
}
